import {
  StyleSheet,
  Text,
  View,
  Dimensions,
  TextInput,
  ScrollView,
  Image,
} from 'react-native'
import React, { useState } from 'react'

const MasterPortrait = (props) => {
  // Lấy navigate và route từ props
  const { navigation, route } = props
  const { navigate, goBack } = navigation

  // Lấy kích thước toàn màn hình
  const screenWidth = Dimensions.get('window').width
  const screenHeight = Dimensions.get('window').height

  const [typeDonut, setTypeDonut] = useState([
    {
      id: 1,
      name: 'Donut',
      isSlected: true,
    },
    {
      id: 2,
      name: 'Pink Donut',
      isSlected: false,
    },
    {
      id: 3,
      name: 'Floating',
      isSlected: false,
    },
  ])

  const [data, setData] = useState([
    {
      id: 1,
      name: 'Tasty donut',
      price: '$10.00',
      image: require('./assets/donut1.png'),
    },
    {
      id: 2,
      name: 'Pink donut',
      price: '$20.00',
      image: require('./assets/donut2.png'),
    },
    {
      id: 3,
      name: 'Floating donut',
      price: '$30.00',
      image: require('./assets/donut3.png'),
    },
    {
      id: 4,
      name: 'Tasty Donut',
      price: '$10.00',
      image: require('./assets/donut4.png'),
    },
    {
      id: 5,
      name: 'Pink Donut',
      price: '$20.00',
      image: require('./assets/donut5.png'),
    },
  ])

  return (
    <View
      style={{
        height: screenHeight,
        width: screenWidth,
        backgroundColor: '#fff',
      }}
    >
      <Text
        style={{
          marginTop: 13,
          marginHorizontal: 15,
          fontWeight: 'bold',
          fontSize: 16,
          color: '#595959',
        }}
      >
        Welcome, Jala!
      </Text>
      <Text
        style={{
          marginTop: 6,
          marginHorizontal: 15,
          fontWeight: 'bold',
          fontSize: 18,
          color: '#161616',
        }}
      >
        Choice you Best food
      </Text>

      <TextInput
        style={{
          marginTop: 20,
          marginHorizontal: 15,
          width: screenWidth * 0.75,
          height: screenHeight * 0.083,
          borderWidth: 1,
          borderColor: '#b5b5b5',
          borderRadius: 3,
          backgroundColor: '#f9f9f9',
          paddingLeft: 13,
          fontWeight: 'bold',
          fontSize: 17,
        }}
        placeholder="Search food"
        placeholderTextColor="#b5b5b5"
      />
      <View
        style={{
          marginTop: 20,
          flexDirection: 'row',
          alignItems: 'center',
          justifyContent: 'space-between',
          marginHorizontal: 15,
        }}
      >
        {typeDonut.map((item, index) => (
          <View
            key={index}
            style={{
              width: screenWidth * 0.27,
              height: screenHeight * 0.05,
              backgroundColor: item.isSlected ? '#f1b000' : '#f9f9f9',
              borderWidth: 1,
              borderColor: '#b5b5b5',
              borderRadius: 5,
              justifyContent: 'center',
              alignItems: 'center',
            }}
          >
            <Text
              style={{
                fontSize: 15,
                fontWeight: 'bold',
                color: '#212121',
              }}
            >
              {item.name}
            </Text>
          </View>
        ))}
      </View>

      <ScrollView
        style={{
          marginBottom: 30,
        }}
      >
        {data.map((item, index) => (
          <View
            style={{
              marginTop: 15,
              marginHorizontal: 15,
              width: screenWidth - 30,
              height: screenHeight * 0.18,
              backgroundColor: '#f4dddd',
              borderRadius: 10,
              flexDirection: 'row',
              alignItems: 'center',
            }}
          >
            <Image
              source={item.image}
              style={{
                height: screenHeight * 0.16,
                width: screenHeight * 0.16,
                borderRadius: 10,
                backgroundColor: '#fff',
                marginHorizontal: screenHeight * 0.01,
              }}
            />
            <View
              style={{
                flex: 1,
                height: screenHeight * 0.16,
                marginHorizontal: 15,
              }}
            >
              <Text
                style={{
                  marginTop: 8,
                  fontSize: 18,
                  fontWeight: 'bold',
                  color: '#000',
                }}
              >
                {item.name}
              </Text>
              <Text
                style={{
                  marginTop: 12,
                  fontSize: 15,
                  fontWeight: 'bold',
                  color: '#828282',
                }}
              >
                Spicy tasty donut family
              </Text>
            </View>
          </View>
        ))}
      </ScrollView>
    </View>
  )
}

export default MasterPortrait

const styles = StyleSheet.create({})
