import { StyleSheet, Text, View, Dimensions, TextInput } from 'react-native'
import React, { useState } from 'react'

const MasterPortrait = (props) => {
  // Lấy navigate và route từ props
  const { navigation, route } = props
  const { navigate, goBack } = navigation

  // Lấy kích thước toàn màn hình
  const screenWidth = Dimensions.get('window').width
  const screenHeight = Dimensions.get('window').height

  const [typeDonut, setTypeDonut] = useState([
    {
      id: 1,
      name: 'Donut',
      isSlected: true,
    },
    {
      id: 2,
      name: 'Pink Donut',
      isSlected: false,
    },
    {
      id: 3,
      name: 'Floating',
      isSlected: false,
    },
  ])

  const [data, setData] = useState([
    {
      id: 1,
      name: 'Tasty donut',
      price: '',
      image: '',
    },
    {
      id: 2,
      name: 'Pink donut',
      price: '',
      image: '',
    },
    {
      id: 3,
      name: 'Floating donut',
      price: '',
      image: '',
    },
    {
      id: 4,
      name: 'Tasty Donut',
      price: '',
      image: '',
    },
    {
      id: 5,
      name: 'Pink Donut',
      price: '',
      image: '',
    },
  ])

  return (
    <View
      style={{
        height: screenHeight,
        width: screenWidth,
        backgroundColor: '#fff',
      }}
    >
      <View
        style={{
          position: 'absolute',
          top: 0,
          backgroundColor: 'red',
          width: screenWidth,
        }}
      >
        <Text
          style={{
            marginTop: 13,
            marginHorizontal: 15,
            fontWeight: 'bold',
            fontSize: 16,
            color: '#595959',
          }}
        >
          Welcome, Jala!
        </Text>
        <Text
          style={{
            marginTop: 6,
            marginHorizontal: 15,
            fontWeight: 'bold',
            fontSize: 18,
            color: '#161616',
          }}
        >
          Choice you Best food
        </Text>

        <TextInput
          style={{
            marginTop: 20,
            marginHorizontal: 15,
            width: screenWidth * 0.75,
            height: screenHeight * 0.06,
            borderWidth: 1,
            borderColor: '#b5b5b5',
            borderRadius: 3,
            backgroundColor: '#f9f9f9',
            paddingLeft: 10,
            fontWeight: 'bold',
          }}
          placeholder="Search food"
          placeholderTextColor="#b5b5b5"
        />
        <View
          style={{
            marginTop: 20,
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'space-between',
            marginHorizontal: 15,
          }}
        >
          {typeDonut.map((item, index) => (
            <View
              key={index}
              style={{
                width: screenWidth * 0.27,
                height: screenHeight * 0.05,
                backgroundColor: item.isSlected ? '#f1b000' : '#f9f9f9',
                borderWidth: 1,
                borderColor: '#b5b5b5',
                borderRadius: 5,
                justifyContent: 'center',
                alignItems: 'center',
              }}
            >
              <Text
                style={{
                  fontSize: 15,
                  fontWeight: 'bold',
                  color: '#212121',
                }}
              >
                {item.name}
              </Text>
            </View>
          ))}
        </View>
      </View>
      <View
        style={{
          marginTop: screenHeight * 0.3,
        }}
      >
        {data.map((item, index) => (
          <View
            style={{
              marginTop: 15,
              marginHorizontal: 15,
              width: screenWidth - 30,
              height: screenHeight * 0.18,
              backgroundColor: '#f4dddd',
              borderRadius: 10,
            }}
          ></View>
        ))}
      </View>
    </View>
  )
}

export default MasterPortrait

const styles = StyleSheet.create({})
