import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  TextInput,
} from 'react-native'
import React from 'react'

const TestREDUX = (props) => {
  const { result } = props
  return (
    <View
      style={{
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <TouchableOpacity
        style={{
          height: 50,
          width: 50,
          backgroundColor: '#f78585',
          justifyContent: 'center',
          alignItems: 'center',
        }}
      >
        <Text>+</Text>
      </TouchableOpacity>
      <TextInput
        style={{
          height: 40,
          borderColor: 'gray',
          borderWidth: 1,
          width: 300,
          marginTop: 20,
          marginBottom: 20,
        }}
      ></TextInput>

      <Text>Result</Text>
    </View>
  )
}
const mapStateToProps = (state) => ({
  result: state.result,
})

export default connect(mapStateToProps)(TestREDUX)

const styles = StyleSheet.create({})
