import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  TextInput,
} from 'react-native'
import React from 'react'
import { useDispatch } from 'react-redux'
import { ACTION1 } from './redux/Constants'

const TestREDUX = (props) => {
  const { result, pluss } = props

  const dispatch = useDispatch()
  return (
    <View
      style={{
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <TouchableOpacity
        style={{
          height: 50,
          width: 50,
          backgroundColor: '#f78585',
          justifyContent: 'center',
          alignItems: 'center',
        }}
        onPress={() => {
          pluss(1)
        }}
      >
        <Text>+</Text>
      </TouchableOpacity>
      <TextInput
        style={{
          height: 40,
          borderColor: 'gray',
          borderWidth: 1,
          width: 300,
          marginTop: 20,
          marginBottom: 20,
        }}
      ></TextInput>

      <Text>Result: {result}</Text>
    </View>
  )
}
const mapStateToProps = (state) => ({
  result: state.result,
})

const mapDispatchToProps = (dispatch) => {
  return {
    pluss: (number) => dispatch({ type: ACTION1, payload: number }),
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(TestREDUX)

const styles = StyleSheet.create({})
