import { StyleSheet, Text, TouchableOpacity, View } from 'react-native'
import React from 'react'

const Test = () => {
  return (
    <View
      style={{
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        flexDirection: 'row',
      }}
    >
      <TouchableOpacity
        style={{
          backgroundColor: '#a9f2ed',
          padding: 10,
        }}
        onPress={() => {}}
      >
        <Text
          style={{
            fontWeight: 'bold',
            fontSize: 18,
          }}
        >
          -
        </Text>
      </TouchableOpacity>
      <Text
        style={{
          fontWeight: 'bold',
          fontSize: 18,
          marginHorizontal: 20,
        }}
      >
        0
      </Text>
      <TouchableOpacity
        style={{
          backgroundColor: '#a9f2ed',
          padding: 10,
        }}
        onPress={() => {}}
      >
        <Text
          style={{
            fontWeight: 'bold',
            fontSize: 18,
          }}
        >
          +
        </Text>
      </TouchableOpacity>
    </View>
  )
}

export default Test

const styles = StyleSheet.create({})
