// Fake API dạng này : https://654efe92358230d8f0ccf0fb.mockapi.io/Login/filter
// Gọi Api về hiển thị dạng ListView hoặc GridView. (Dạng 2)
// (Dạng 3)  Filter ô TextInput dạng chữ.
// Còn filter Type dạng nút bấm.

// (Dạng 4) Thêm Xóa sửa mảng.

import {
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
  ScrollView,
} from 'react-native'
import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { TextInput } from 'react-native'

const Dang2_3_4 = () => {
  const [data, setData] = useState([]) // Dữ liệu từ Fake API

  const getData = () => {
    axios
      .get('https://654efe92358230d8f0ccf0fb.mockapi.io/Login/filter')
      .then((response) => {
        setData(response.data)
      })
      .catch((error) => {
        console.error(error)
      })
  }

  useEffect(() => {
    getData()
  }, [])
  return (
    <View
      style={{
        flex: 1,
        backgroundColor: '#feffea',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <View
        style={{
          height: 200,
          width: 200,
          backgroundColor: 'red',
        }}
      >
        <TextInput
          onChangeText={(text) => {}}
          style={{
            width: screenWidth * 0.73,
            height: screenHeight * 0.073,
            borderWidth: 1,
            backgroundColor: '#fff',
            borderRadius: 3,
            paddingLeft: 13,
          }}
          placeholder="Nhập tên Note"
          placeholderTextColor="#111"
        />
      </View>

      <ScrollView>
        {data.map((item) => {
          return (
            <View
              style={{
                marginTop: 20,
                width: 300,
                backgroundColor: '#fff',
                flexDirection: 'row',
                justifyContent: 'space-between',
              }}
            >
              <View>
                <Text
                  style={{
                    marginTop: 20,
                    fontSize: 15,
                    fontWeight: 'bold',
                    color: '#111',
                  }}
                >
                  {item.name}
                </Text>
                <Text
                  style={{
                    marginTop: 20,
                    fontSize: 15,
                    fontWeight: 'bold',
                    color: '#111',
                  }}
                >
                  {item.type}
                </Text>
                <Text
                  style={{
                    marginTop: 20,
                    fontSize: 15,
                    fontWeight: 'bold',
                    color: '#111',
                  }}
                >
                  {item.description}
                </Text>
              </View>

              <TouchableOpacity
                style={{
                  height: 60,
                  width: 60,
                  color: 'red',
                }}
              >
                <Text>Xóa</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={{
                  height: 60,
                  width: 60,
                  color: 'yellow',
                }}
              >
                <Text>sửa</Text>
              </TouchableOpacity>
            </View>
          )
        })}
        <TouchableOpacity
          style={{
            marginTop: 30,
            marginBottom: 30,
            width: 300,
            height: 45,
            backgroundColor: '#ff8eb9',
            justifyContent: 'center',
            alignItems: 'center',
          }}
        >
          <Text>Thêm</Text>
        </TouchableOpacity>
      </ScrollView>
    </View>
  )
}

export default Dang2_3_4

const styles = StyleSheet.create({})
