import { StyleSheet, Text, TouchableOpacity, View } from 'react-native'
import React, { useEffect, useState } from 'react'
import axios from 'axios'

const Dang2_3 = () => {
  const [data, setData] = useState([]) // Dữ liệu từ Fake API

  const getData = () => {
    axios
      .get('https://654efe92358230d8f0ccf0fb.mockapi.io/Login/filter')
      .then((response) => {
        setData(response.data)
      })
      .catch((error) => {
        console.error(error)
      })
  }

  useEffect(() => {
    getData()
  }, [])
  return (
    <View
      style={{
        flex: 1,
        backgroundColor: '#feffea',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      {data.map((item) => {
        return (
          <View
            style={{
              height: 60,
              width: 300,
              backgroundColor: '#fff',
              flexDirection: 'row',
              justifyContent: 'space-between',
            }}
          >
            <View>
              <Text
                style={{
                  marginTop: 20,
                  fontSize: 15,
                  fontWeight: 'bold',
                  color: '#111',
                }}
              >
                {item.name}
              </Text>
              <Text
                style={{
                  marginTop: 20,
                  fontSize: 15,
                  fontWeight: 'bold',
                  color: '#111',
                }}
              >
                {item.type}
              </Text>
              <Text
                style={{
                  marginTop: 20,
                  fontSize: 15,
                  fontWeight: 'bold',
                  color: '#111',
                }}
              >
                {item.description}
              </Text>
            </View>

            <TouchableOpacity
              style={{
                height: 60,
                width: 60,
                color: 'red',
              }}
            >
              <Text>Xóa</Text>
            </TouchableOpacity>
            <TouchableOpacity
              style={{
                height: 60,
                width: 60,
                color: 'yellow',
              }}
            >
              <Text>sửa</Text>
            </TouchableOpacity>
          </View>
        )
      })}
    </View>
  )
}

export default Dang2_3

const styles = StyleSheet.create({})
