import { StyleSheet, Text, View } from 'react-native'
import React, { useState } from 'react'

const Man2 = () => {
  const [name, setName] = useState('')
  const [description, setDescription] = useState('')
  return (
    <View
      style={{
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <Text
        style={{
          fontSize: 30,
          fontWeight: 'bold',
          color: '#111',
        }}
      >
        Thêm
      </Text>
      <TextInput
        onChangeText={(text) => {
          setName(text)
        }}
        style={{
          width: screenWidth * 0.73,
          height: screenHeight * 0.073,
          borderWidth: 1,
          backgroundColor: '#fff',
          borderRadius: 3,
          paddingLeft: 13,
        }}
        placeholder="Nhập tên ..."
        placeholderTextColor="#111"
      />
      <TextInput
        onChangeText={(text) => {
          setDescription(text)
        }}
        style={{
          width: screenWidth * 0.73,
          height: screenHeight * 0.073,
          borderWidth: 1,
          backgroundColor: '#fff',
          borderRadius: 3,
          paddingLeft: 13,
        }}
        placeholder="Nhập tên ..."
        placeholderTextColor="#111"
      />
    </View>
  )
}

export default Man2

const styles = StyleSheet.create({})
